targets.query:
from(bucket: "njmon")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["host"] =~ /^${HOST}$/)
  |> filter(fn: (r) => r["_measurement"] == "paging_spaces")
  |> filter(fn: (r) => r["_field"] == "mb_size")
  |> distinct(column: "_value")
  |> map(fn: (r) => ({
      r with _value: r["_value"] / 1024
  }))

from(bucket: "njmon")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["host"] =~ /^${HOST}$/)
  |> filter(fn: (r) => r["_measurement"] == "config")
  |> filter(fn: (r) => r["_field"] == "mem_online")
  |> distinct(column: "_value")
  |> map(fn: (r) => ({
      r with _value: r["_value"] / 1024
  }))

from(bucket: "njmon")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["host"] =~ /^${HOST}$/)
  |> filter(fn: (r) => r["_measurement"] == "identity")
  |> filter(fn: (r) => r["_field"] == "ipaddress")
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> rename(columns: {
      ipaddress: "ipaddress"
  })
  |> keep(columns: ["ipaddress"])

from(bucket: "njmon")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["host"] =~ /^${HOST}$/)
  |> filter(fn: (r) => r["_measurement"] == "server")
  |> filter(fn: (r) => r["_field"] == "fwversion")
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> rename(columns: {
      fwversion: "fwversion"
  })
  |> keep(columns: ["fwversion"])
